// Sliding window rate limiter
async function checkRateLimit(userId, limit, window) {
  const now = Date.now();
  const key = `ratelimit:${userId}`;

  const multi = redis.multi();
  multi.zremrangebyscore(key, 0, now - window);
  multi.zadd(key, now, `${now}-${Math.random()}`);
  multi.zcard(key);
  multi.expire(key, Math.ceil(window / 1000));

  const results = await multi.exec();
  const count = results[2][1];

  return { allowed: count <= limit, remaining: Math.max(0, limit - count) };
}

// Application status update with notification
async function updateApplicationStatus(appId, newStatus) {
  const app = await db.application.update({
    where: { id: appId },
    data: { 
      status: newStatus,
      updatedAt: new Date()
    },
    include: { user: true, company: true }
  });

  if (newStatus === 'INTERVIEW') {
    await sendNotification(app.user.email, {
      type: 'INTERVIEW_SCHEDULED',
      company: app.company.name
    });
  }

  return app;
}
